const hb = (typeof context !== "undefined" && context.handlebars) ? context.handlebars : handlebars;

hb.registerHelper("yesno", (v) => v ? "enabled" : "disabled");

hb.registerHelper("formatTs", (val) => {
  if (val == null || val === "") return "—";
  const n = Number(val);
  const ms = n > 1e12 ? n : n * 1000;
  const tzSetting = (typeof context !== "undefined" && context.grafana) ? context.grafana.timeZone : "browser";
  const zone = tzSetting === "browser" ? Intl.DateTimeFormat().resolvedOptions().timeZone : tzSetting;
  return new Date(ms).toLocaleString(undefined, {
    timeZone: zone, hour12: false,
    year: "numeric", month: "2-digit", day: "2-digit",
    hour: "2-digit", minute: "2-digit", second: "2-digit"
  });
});

hb.registerHelper("formatDuration", (secs) => {
  if (secs == null || secs === "") return "—";
  const n = Number(secs);
  const h = Math.floor(n / 3600), m = Math.floor((n % 3600) / 60), s = n % 60;
  const parts = []; if (h) parts.push(`${h}h`); if (m) parts.push(`${m}m`); parts.push(`${s.toFixed(3)}s`);
  return parts.join(" ");
});

hb.registerHelper("formatBytes", (b) => {
  if (b == null || b === "") return "—";
  const n = Number(b); if (!isFinite(n)) return String(b);
  const u = ["B", "KB", "MB", "GB", "TB", "PB"]; const i = n > 0 ? Math.floor(Math.log(n) / Math.log(1024)) : 0;
  const v = (n / Math.pow(1024, i)).toFixed(i ? 2 : 0); return `${v} ${u[i]}`;
});

hb.registerHelper("newline", (s) => (s ?? "").replaceAll("\\n", "\n"));

if (!hb.helpers.match) {
  hb.registerHelper("match", (str, pattern) => new RegExp(pattern).test(String(str)));
}
